import React, { useState, useEffect } from 'react';
import {View, Text, TouchableOpacity} from 'react-native';
import Icon from 'react-native-vector-icons/AntDesign';
import Routes from '../../componentes/menu/routes';
import {useNavigation} from '@react-navigation/native';
import styles from './style'
import AsyncStorage from '@react-native-async-storage/async-storage';




export default function Favoritos(){ 
    const navigation = useNavigation();
    const [dados, setDados] = useState([]);
    async function buscarFavoritos() {
      try {
        // Obtém o token de AsyncStorage
        const token = await AsyncStorage.getItem("token");
          
      
        if (token) {
          // Construa o cabeçalho Authorization
          const headers = {
            'Content-type': 'application/json; charset=UTF-8',
            'Authorization': `Bearer ${token}`
          };
    
          // Faça a solicitação usando o cabeçalho personalizado
          const response = await fetch('https://tcc-production-e100.up.railway.app/api/lazer/lazer', {
            method: 'GET', // ou outro método HTTP
            headers: headers
          });
    
          if (response.status === 200) {
            const data = await response.json();
            console.log("Dados da resposta:", data);
            setDados(data)
          } else {
            console.error("Erro na solicitação:", response.status);
          }
        } else {
          console.log("Token não encontrado em AsyncStorage.");
        }
      } catch (error) {
        console.error("Erro ao fazer a solicitação:", error);
      }
    }
    

    buscarFavoritos();

      return(
        <View style= {{flex:1, backgroundColor: '#FFF'}}>
            <TouchableOpacity style={styles.botaopular} onPress={ () => navigation.navigate('TelaInicial')} >
                <Text style={{color: '#000',fontSize: 35, left: 30, marginTop: 60}}><Icon name="leftcircle" size={40} color='#17A558'/>  Favoritos</Text>
            </TouchableOpacity>


            {dados.map((item, index) => (
        <TouchableOpacity
          key={item.idLazer}
          style={{ height: 160 }}
          onPress={ () => navigation.navigate('TelaDetalhes',item)}
        >
          <View
            style={{
              backgroundColor: '#B1D3C1',
              marginTop: 10,
              borderRadius: 35,
              width: 340,
              height: 150,
              marginLeft: 9,
            }}
          >
             <Image  source={{uri: item.imagem}} style={styles.Imagens} />
            <Text
              style={{
                marginLeft: 140,
                marginTop: -125,
                fontSize: 18,
              }}
            >
              {item.nome}
            </Text>

            <Text
              style={{
                marginLeft: 140,
                marginTop: 3,
                fontSize: 12,
              }}
            >
              {item.descricao}
            </Text>
          </View>

        
        </TouchableOpacity>
      ))}

            <Routes></Routes>
        </View>
        
      );
      
}
