import React from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  Image,
  Alert,
  Platform,
  PermissionsAndroid,
} from 'react-native';


const ImagePickerComponent = () => {
  const [imageUri, setImageUri] = ('');

  const handlePickImage = async () => {
    if (Platform.OS === 'android') {
      // Solicitar permissão para acessar a galeria no Android
      const granted = await PermissionsAndroid.request(
        PermissionsAndroid.PERMISSIONS.READ_EXTERNAL_STORAGE,
        {
          title: 'Permissão para acessar a galeria',
          message: 'Este aplicativo precisa de permissão para acessar sua galeria.',
          buttonPositive: 'OK',
        }
      );

      if (granted !== PermissionsAndroid.RESULTS.GRANTED) {
        Alert.alert('Permissão negada', 'Você precisa conceder permissão para acessar a galeria.');
        return;
      }
    }

    // Opções para a seleção da imagem
    const options = {
      title: 'Selecione uma imagem',
      storageOptions: {
        skipBackup: true,
        path: 'images',
      },
    };

    // Mostrar a galeria e obter a imagem
    ImagePicker.showImagePicker(options, (response) => {
      if (response.didCancel) {
        console.log('Seleção de imagem cancelada');
      } else if (response.error) {
        console.error('Erro ao selecionar imagem:', response.error);
      } else {
        // Atualizar o estado com a URI da imagem selecionada
        setImageUri(response.uri);
      }
    });
  };

  return (
    <View>
      <TouchableOpacity onPress={handlePickImage}>
        <Text>Selecionar Imagem</Text>
      </TouchableOpacity>
      {imageUri && (
        <View>
          <Text>Imagem Selecionada:</Text>
          <Image source={{ uri: imageUri }} style={{ width: 200, height: 200 }} />
        </View>
      )}
    </View>
  );
};

export default ImagePickerComponent;
